package org.example.required4testing.services;import jakarta.inject.Inject;import org.example.required4testing.common.Result;import org.example.required4testing.dtos.UserDto;import org.example.required4testing.models.User;import org.example.required4testing.repositories.tests.UserRepository;import org.springframework.stereotype.Service;@Servicepublic class LoginService {    @Inject    private UserRepository userRepository;    public Result<UserDto> validate(String username, String password) {        var user = userRepository.findByName(username);        return userRepository.findByName(username)                .filter(u -> u.getPassword().equals(password))                .map(u -> new Result<>(true, new UserDto(user.get().getName(), user.get().getLevel())))                .orElse(new Result<>(false, null));    }}